                <script type="text/javascript">                    //Ajax                    $(document).ready(function () {                       $(".delete").click(function(e){                            e.preventDefault();                            var id = $(this).attr('id');                            var c_url = "<?php echo $this->config->base_url().'admin/news' ?>";                            var con = confirm("Are you sure?");                            if(con){                                $.ajax({                                    url: c_url,                                    type: 'post',                                    data: {'action': 'delete_news', 'news_id': id},                                    success: function (data, status) {                                        var result = String(data);                                        result = result.trim();                                        if (result == "1") {                                            window.location.href = "<?php echo $this->config->base_url().'admin/news' ?>";                                        }                                        else {                                            alert('failed');                                        }                                    },                                    error: function (xhr, desc, err) {                                        alert('failed');                                    }                                });//end ajax call                            }                        });                       $(".publish").click(function(e){                            e.preventDefault();                            var id = $(this).attr('id');                            var c_url = "<?php echo $this->config->base_url().'admin/news' ?>";                            var con = confirm("Are you sure you wanna publish this news?");                            if(con){                                $.ajax({                                    url: c_url,                                    type: 'post',                                    data: {'action': 'publish', 'news_id': id},                                    success: function (data, status) {                                        var result = String(data);                                        result = result.trim();                                        if (result == "1") {                                            window.location.href = "<?php echo $this->config->base_url().'admin/news' ?>";                                        }                                        else {                                            alert('failed');                                        }                                    },                                    error: function (xhr, desc, err) {                                        alert('failed');                                    }                                });//end ajax call                            }                        });                       $(".unpublish").click(function(e){                            e.preventDefault();                            var id = $(this).attr('id');                            var c_url = "<?php echo $this->config->base_url().'admin/news' ?>";                            var con = confirm("Are you sure you wanna unpublish this news?");                            if(con){                                $.ajax({                                    url: c_url,                                    type: 'post',                                    data: {'action': 'unpublish', 'news_id': id},                                    success: function (data, status) {                                        var result = String(data);                                        result = result.trim();                                        if (result == "1") {                                            window.location.href = "<?php echo $this->config->base_url().'admin/news' ?>";                                        }                                        else {                                            alert('failed');                                        }                                    },                                    error: function (xhr, desc, err) {                                        alert('failed');                                    }                                });//end ajax call                            }                        });                    });                </script>                <!-- Main Container -->                <main id="main-container">                    <!-- Page Header -->                    <div class="bg-image overflow-hidden" style="background-image: url('<?php echo $this->config->item("assets_url"); ?>/img/banners/bg3.png');">                        <div class="bg-black-op">                            <div class="content content-narrow">                                <div class="block block-transparent">                                    <div class="block-content block-content-full">                                        <div class="row">                                            <div class="col-sm-6">                                                <h1 class="h1 font-w300 text-white animated fadeInDown push-50-t push-5">News Center</h1>                                            </div>                                            <div class="col-sm-6">                                               <button data-toggle="modal" class='btn btn-primary push-50-t push-5 pull-right add-new' href="#modal-news"><i class='fa fa-plus'></i> Create New</button>";                                            </div>                                        </div>                                                                                                                    </div>                                </div>                            </div>                        </div>                    </div>                    <!-- END Page Header -->                    <!-- Page Content -->                    <div class="content">                        <div class="row">                        <?php                        foreach ($list->result() as $r) {                        ?>                            <div class='col-lg-6'>                                <div class='block block-themed'>                                    <div class='block-header bg-primary-dark'>                                        <ul class='block-options'>                                            <?php                                                if($r->is_published == 0){                                                    echo "<li><button type='button' class='publish' id='$r->id'><i class='fa fa-send'></i></button></li>";                                                }                                                else{                                                    echo "<li><button type='button' class='unpublish' id='$r->id'><i class='fa fa-close'></i></button></li>";                                                }                                            ?>                                                                                        <li>                                                <button type='button' class='delete' <?php echo "id='$r->id'"; ?> ><i class='fa fa-trash'></i></button>                                            </li>                                            <li>                                                <button type='button' data-toggle='block-option' data-action='content_toggle'><i class='si si-arrow-up'></i></button>                                            </li>                                        </ul>                                        <h3 class='block-title'><?php echo $r->title; ?></h3>                                    </div>                                    <div class='block-content'>                                        <p>                                            Status : <?php echo ($r->is_published == 0) ? "UNPUBLISHED" : "PUBLISHED"?> <br/>                                        </p>                                        <p class='text-muted'>                                            Published : <?php echo $r->date_created; ?>                                        </p>                                        <p>                                            <?php echo $r->content; ?>                                        </p>                                    </div>                                </div>                            </div>                        <?php                        }                        ?>                        </div>                    </div>                    <!-- END Page Content -->                </main>                <!-- END Main Container -->                </div>                <!-- News Modal -->                <div class="modal fade" id="modal-news" tabindex="-1" role="dialog" aria-hidden="true">                    <div class="modal-dialog">                        <div class="modal-content">                            <div class="block block-themed block-transparent remove-margin-b">                                <div class="block-header bg-danger">                                    <ul class="block-options">                                        <li>                                            <button data-dismiss="modal" type="button"><i class="si si-close"></i></button>                                        </li>                                    </ul>                                    <h3 class="block-title">News Form</h3>                                </div>                                <div class="block-content">